        let maxValue = 0;

        for(const item of array){ 
            let diff = capacity-runningWt;          
            if(diff>=item.wt){
                runningWt += item.wt;
                maxValue += item.value
            }else{
                runningWt += diff;
                maxValue += diff*item.valuePerWt
            }
        }

        return maxValue;

