// Create a cancelable promise to terminate unwanted async tasks.
const api = (success, delay) => {
  let timer;
  const promise = new Promise((resolve, reject) => {
    timer = setTimeout(() => {
      if (success) resolve("data fetched succesfully");
      else reject("data fetch failed");
    }, delay);
  });

  promise.cancel = () => clearTimeout(timer);
  return promise;
};

async function cancellable(api, cancel) {
  return new Promise((resolve, reject) => {
