class EventEmitter {
  constructor() {
    this.events = {};
  }
  emit(event, ...args) {
    if (this.events[event]) {
      this.events[event].forEach((list) => {
        Promise.resolve().then(list(...args));
      });
    }
  }

  on(event, fn) {
    if (!this.events[event]) {
      this.events[event] = [];
    }
    this.events[event].push(fn);
  }
  once(event, fn) {
    const wrapper = (...args) => {
      fn(...args);
      this.off(event, wrapper);
    };
    if (!this.events[event]) {
      this.events[event] = [];
    }
    this.events[event].push(wrapper);
  }

  off(event, list) {
    this.events[event] = this.events[event].filter(
      (listner) => listner != list,
    );
  }
}

